            try {
                this.cargando = true
                const {status,data} = await login(this.form)
                this.cargando = false
                if(status === 200){
                    await this.$store.dispatch('auth/saveToken',data)
                    await this.$store.dispatch('auth/fetchUser')
                    this.$router.push({name: 'Home'})
                }   
            } catch (error) {
                this.cargando = false
                console.error(error);
            }
        }
    }
}
</script>

